INSERT INTO scripts (scene_id, type, speaker, description) VALUES (2, 'description', NULL, 'ヘッドフォンに、サングラスのツーリングスタイルの植村一也（24）。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (2, 'description', NULL, 'ロードバイクのペダルを踏み、風を切って走っていく一也に『アバンタイトル』がかぶる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, '空と海が広がっている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, '軽快に走ってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, 'と、横道から、フィンを乗せたマウンテンバイクがイン！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, 'あっという間に一也を抜き去るマウンテンバイク。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, 'マウンテンバイクに乗る少年？らしき背中。ミチルである。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, 'まるで少年にしか見えない服装と髪型をしている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, '一也スピードをあげ、ミチルを追い抜いていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, 'と、ミチルもスピードを上げ、追いつき、追い抜く。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, '一也、意地になって加速し、猛スピードで一気にミチルを抜き去る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, 'ロードバイクを止め、今来た道を振り返る一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, 'ミチルの姿はない。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (3, 'description', NULL, '一也、再びペダルを踏み走っていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (4, 'description', NULL, 'ジンベエザメの巨体が、悠然と通り過ぎる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (4, 'description', NULL, '続いて、オニイトマキエイが飛ぶように泳いでいく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (4, 'description', NULL, '縦横無尽に泳いでいく魚たち。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (4, 'description', NULL, '巨大なアクリルガラスの向こうは、まさに海洋のようだ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (4, 'description', NULL, 'その前で、胡坐をかいて静かに魚たちを見つめている福原謙三（67）。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (4, 'description', NULL, 'この水族館の館長である。そこへ、係員がやって来て、');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (4, 'dialogue', '係員', '館長、そろそろ会議の時間です');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (4, 'description', NULL, 'わかった、と手を上げる福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (5, 'description', NULL, 'ロードバイクに乗った一也、水族館へ入っていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (5, 'dialogue', '係員', '自転車ダメですよ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (5, 'description', NULL, 'ヘッドホンの一也、注意に気づかず、ロードバイクを担ぐとエスカレーターを降りていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (5, 'description', NULL, '一也の目の前に、イルカプール、いるかスタジオ、オキちゃん劇場等が点在、さらにその向こうには、どこまでも青い海が広がっている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (5, 'dialogue', '一也', '（思わず見とれ）……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (5, 'description', NULL, 'エスカレーターを上がってくる福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (5, 'description', NULL, '一也、景色に見とれたまま、降りて行く。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (5, 'description', NULL, '福原、一也を見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (5, 'description', NULL, 'すれ違う二人。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (6, 'description', NULL, '立ち入り禁止ゾーンに入っていく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (6, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (6, 'description', NULL, 'やってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (6, 'description', NULL, 'プールサイドで、イルカを見ているミチルに気づく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (6, 'dialogue', '一也', '君、ここ関係者以外立ち入り……、あ、お前さっきの');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (6, 'description', NULL, 'プイッと去っていくミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (6, 'dialogue', '一也', '（何だ、あいつ……？）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'description', NULL, '入ってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'dialogue', '一也', 'おはようございます');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'description', NULL, '誰もいない。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'description', NULL, 'と、奥の机から顔を上げる、海獣課・仲村圭一課長（38）。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'dialogue', '一也', '(会釈して)……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'description', NULL, '一也が手持ちぶさたに立っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'description', NULL, '奥の部屋から仲村が現われて、');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'dialogue', '仲村', 'はいこれ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'description', NULL, 'スタッフのポロシャツを一也に差し出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'description', NULL, 'え？と受け取る一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'dialogue', '仲村', '机は、あっちの片隅を使って');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'description', NULL, '指差した一角には、何もない空机。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'dialogue', '仲村', '着替えたらみんなに紹介するから');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'description', NULL, '一也、スタッフ用のポロシャツの意味がわからず目で訴えるが');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (7, 'dialogue', '仲村', 'あ、更衣室はそこ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'description', NULL, 'プールサイドにしゃがんでイルカの様子を見ている比嘉剛（25）と望月ユリ（25）。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'description', NULL, '真新しいポロシャツに着替えた一也と仲村が階段を上がってくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'description', NULL, '仲村、二人に声をかけ一也を紹介する。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'dialogue', '仲村', '今日からウチで働く獣医の植村一也君');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'dialogue', '一也', 'よろしくお願いします（頭を下げる）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'dialogue', '仲村', '飼育員の望月ユリと比嘉剛。飼育のことはこの２人に聞いて覚えるように');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'dialogue', '一也', 'え、飼育？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'dialogue', '仲村', 'えーと、次は……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'description', NULL, 'さっさと行ってしまう仲村。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'dialogue', '一也', 'え、あ、あの、オレ獣医……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'description', NULL, '言いながら後を追う一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'dialogue', '剛', 'あれ？　今年になって辞めたの、３人だっけ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'dialogue', 'ユリ', '４人。だから、５番目の獣医');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'dialogue', '剛', 'いつまでもつかな……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (8, 'description', NULL, '二人の見つめる先、他の飼育員たちに一也が紹介されている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (9, 'description', NULL, 'ユリ、飼育員たちに混じって、一也がおぼつかない手つきでトビウオを切っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (9, 'description', NULL, 'ユリが、新たなトロ箱をまな板に乗せる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (9, 'dialogue', '一也', '……まだ切るんですか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (9, 'dialogue', 'ユリ', '１頭で１日10キロ、20頭いるから２００キロ以上。それを６回にわけて食べさせるのよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (9, 'dialogue', '一也', '１日中餌切りと餌やりですか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (9, 'dialogue', '剛', '１年中だよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (9, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (9, 'dialogue', '剛', 'ヒレちゃんと落とせよ。イルカが喉詰まらすから');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (9, 'description', NULL, 'テキパキとトビウオを処理していく剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (9, 'description', NULL, '一也、何か言いたい気持ちを抑えて餌切りに戻る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, '水が落とされたプールにイルカが４頭。どじょうのように体をくねらせている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, 'ヘルメットに胴付ゴム長靴姿の一也が、ハシゴを降りていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, '剛、ユリ、飼育員Ａ、Ｂ、Ｃが、専用担架にイルカを乗せようとしている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', '仲村', 'ウチにはイルカが全部で20頭いる。バンドウイルカが５頭、オキゴンドウが６頭、ミナミバンドウが６頭、カマイルカが２頭、シワハイルカが１頭だ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', '一也', '……はぁ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', '仲村', '今日中にイルカの顔と名前を覚えるように');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, '目の前のプールには、４頭のバンドウイルカ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', '一也', '（４頭を見渡し）みんな同じ顔じゃないっすか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', '仲村', '傷や体の色で見分けろ。右からポイ、コニー、ダン、フジ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, '専用担架に乗せられたイルカがクレーンで吊り上げられ、分銅を用いた天秤ばかりで体重測定される。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, '最後に一人残ってプールを掃除している一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, '汗だくで、デッキブラシを壁面にこすりつけている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', '一也', '（何でこんなことしなきゃ……）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, 'と、突然、頭の上に滝のような水が降ってくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', '一也', 'うわっ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, '水流で床に投げ出され、フジの目の前に来る一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, 'フジ、バタバタと尾びれを振りながら逃げていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'description', NULL, '一也、立ち上がりハシゴを昇っていくと、ユリが手を貸す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', 'ユリ', '植村君、ビッグマザーに嫌われちゃったね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', '一也', 'ビッグマザー？　そんな名前いましたっけ？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (10, 'dialogue', 'ユリ', 'フジのことよ。芸は苦手だけど３頭の子供を産んだ子育ての名人、だからビッグマザー');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (11, 'description', NULL, '朝の元気と裏腹に、ロードバイクをトロトロとこいで帰ってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (11, 'description', NULL, 'ロードバイクを降りると、背後から声がかかる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (11, 'dialogue', '一也', '(振り向き)あっ、お前……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (11, 'description', NULL, 'ミチル、黙って泡盛の瓶を突き出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (11, 'dialogue', '一也', 'え……？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (11, 'dialogue', 'ミチル', 'オジイから引っ越し祝い');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (11, 'description', NULL, '踵を返して、向かいの家に走っていくミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (11, 'dialogue', '一也', 'オジイって誰……、おいっ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (11, 'description', NULL, '向かいの家の縁側で、クバの葉を編みながらサバニのアカ汲みを作っている玉城のオジイ。一也をチラリと見る。傍らには泡盛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (11, 'dialogue', '一也', '……（ペコッと頭を下げる）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'description', NULL, '餌やりをしながらトレーニングをするユリ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'description', NULL, '餌のバケツを持った一也が来る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'description', NULL, 'と、海獣課の方へ歩いて来るミチルに気づく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'dialogue', '一也', '(あれ？　またあいつ……)');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'dialogue', 'ユリ', 'どうしたの？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'dialogue', '一也', 'あ、いや');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'description', NULL, '一也、フジに餌やりを始めようとすると、海獣課から出てきた福原がミチルと話しているのが見える。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'dialogue', 'ユリ', '(気づいて)あの子、ミチルっていうのよ。館長が昔から世話になっている漁師さんの孫なの');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'description', NULL, 'ミチルに親しげに話しかける福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (12, 'dialogue', 'ユリ', '親の離婚でオジイに預けられたまま、学校にも行ってないみたい。いっつもフジに会いに来てんだよね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (13, 'description', NULL, '目覚まし時計が鳴る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (13, 'description', NULL, '１つ鳴り、２つ鳴り、その数が増えていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (13, 'description', NULL, 'もぞもぞと一也の手が伸びてきて、目覚まし時計を探す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (14, 'description', NULL, '慌てて出てくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (14, 'description', NULL, 'ロードバイクに飛び乗り、走り出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (15, 'description', NULL, '満員の観客の前、飼育員たちがショーを行っている。見事な技を披露しているイルカたち。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (15, 'description', NULL, '観客から大きな拍手と歓声があがる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (16, 'description', NULL, '餌のバケツを積んだリヤカーを、走って引いていく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (17, 'description', NULL, 'ステージ裏から、バケツを持った一也が慌ててやってくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (17, 'description', NULL, 'と、ツルッと滑り、転倒。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (17, 'description', NULL, 'バケツを引っくり返し、餌がステージに散乱する。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (17, 'description', NULL, 'イルカたちが、その餌を食べようとステージに乗り上げてきて、一也に迫る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (17, 'dialogue', '一也', '！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (17, 'description', NULL, '大喜びの観客たち。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (18, 'description', NULL, '空のバケツを積んだリヤカーを引いていく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (18, 'description', NULL, '丸窓のところにやってきたフジ、一也を見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (18, 'description', NULL, 'ふと、丸窓に目を向ける一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (18, 'description', NULL, '笑っているようなフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (18, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (19, 'description', NULL, '剛、ユリがイルカを横たわらせ、体温計を肛門に挿入する。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (19, 'description', NULL, 'ペンでメモを取っている一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (19, 'description', NULL, 'と、過ってペンをプールに落とす。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (19, 'dialogue', '一也', 'あ、ああ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (19, 'description', NULL, 'うろたえる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (19, 'description', NULL, 'すぐさまプールに飛び込むユリ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (19, 'dialogue', '剛', '何やってんだお前！　イルカが飲み込んだらどうすんだ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (19, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (19, 'description', NULL, '上がってきたユリ、無言でペンを一也に突き出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (19, 'dialogue', '一也', 'すみません……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'description', NULL, '流しの前にしゃがみ込み、一也が１人黙々とバケツを洗っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'description', NULL, '周りには大量のバケツが並んでいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'description', NULL, 'と、仲村が顔を出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '仲村', 'どうね、これだけあると楽しいだろ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '一也', '……課長、オレいつまでこんなことやんなきゃなんないんですか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '仲村', 'ん？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '一也', '餌切りに餌やり。プール掃除にバケツ洗い。全部飼育員の仕事じゃないですか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '仲村', 'じゃあ、お前は何がやりたい？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '一也', 'イルカの治療をしたり、検査のデータを分析したり、もっと他に――');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '仲村', 'お前、イルカのことどれだけわかってる！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '仲村', '（厳しい顔で）飼育ができて、しっかりイルカと付き合えなければ、治療も検査もできやしない');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '仲村', 'データを見るだけの獣医なら、ウチはいらない');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (20, 'dialogue', '仲村', '（ニコッと顔を崩して）それが館長の方針だ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (21, 'description', NULL, '大声を上げ、全速力でロードバイクをこいでいく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (21, 'description', NULL, 'ほとばしる汗。息が切れる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (21, 'description', NULL, '倒れこむようにロードバイクを降りると、地面に寝転がり空を仰ぐ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'description', NULL, 'カウンターの中に七海（30）。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'description', NULL, 'グラスに入った泡盛のロックを仲村の前に置く。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'description', NULL, 'もう片方の手に何か持っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'dialogue', '仲村', '何ね、これ？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'dialogue', '七海', '来週、海亀の放流会あるでしょ。その時どうかなと思って');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'dialogue', '仲村', '……え？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'description', NULL, '海亀の被り物を見つめる仲村。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'dialogue', '七海', 'かぶってみて');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'dialogue', '仲村', '……どんなして？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'description', NULL, '七海、黙ってかぶってみせる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (22, 'dialogue', '仲村', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (23, 'description', NULL, '机に突っ伏して眠ってしまっている一也、ハッと目を覚ます。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (23, 'description', NULL, '皆帰ったのか、誰もいない。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (23, 'description', NULL, '机の上で、携帯がブルブルしている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (23, 'description', NULL, '携帯を手に取り、メールを開く一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (23, 'description', NULL, '『元気？　仕事には慣れた？　こっちは忙しいながらも毎日充実してます！　一也も頑張ってね。応援してるよ。　陽子』');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (23, 'description', NULL, '写メールを開くと、陽子が笑っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (23, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (24, 'description', NULL, '部屋のあちこちに、魚の置物や標本があふれている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (24, 'description', NULL, '本や資料の山で埋もれているデスクで愛妻弁当をかきこみながら研究論文を書いている福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (24, 'description', NULL, 'と、叫び声が聞こえる。ビクッと反応する福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'description', NULL, '月明かりの中、叫びながら水の中にダイブする一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'description', NULL, '気持ちよさそうに顔を上げると、水面にポカリと浮かんでみせる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'description', NULL, 'と、近くの水面がさざ波立つ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'description', NULL, '見ると、フジが水面からひょっこり顔を出している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'description', NULL, '黒いつぶらな瞳でじっと一也を見ているフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'dialogue', '一也', 'フジ……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'description', NULL, '一也の周りをグルグル回るフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'description', NULL, '遊んで、というように一也に寄ってくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'description', NULL, '自然に笑みが浮かんでくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'description', NULL, '応えるようにフジの体を触ったり、撫でたりする。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (25, 'description', NULL, 'いつまでも水の中で戯れている一也とフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (26, 'description', NULL, '窓から、イルカラグーンを見下ろしている福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (27, 'dialogue', '仲村', 'まず、持ち方はですね――');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (27, 'description', NULL, '海亀の被り物をした仲村が説明する中、地元の小学生や留学生たちが、海亀の放流会に参加している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (27, 'description', NULL, '一也、剛、ユリ、飼育員たちの先導で、海亀の子供たちが海へ放たれる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (27, 'description', NULL, '離れたところから、ミチルが見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (27, 'description', NULL, '大海原に向かって泳いでいく海亀の子供たち。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'description', NULL, '“黒潮の海”で悠々と泳ぐ魚たち。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'description', NULL, '水槽の上では、ジンベエザメの餌やりが行われている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'description', NULL, '福原がその様子を説明しながら外国人客を案内している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'description', NULL, '書類を手に歩いてきた一也、軽く会釈し、通り過ぎようとする。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '福原', '植村');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'description', NULL, '広報課長に外国人客を任せる福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '福原', 'どうだ、やってるか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '一也', 'はい。何とかやってます');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '福原', 'そうか、まだもってるか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '福原', 'ま、お前も、結構ヤナワラバーだからな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '一也', '？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '福原', 'オレも、接待なんかより魚の相手していてーよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '一也', '（思わず笑う。）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'description', NULL, '笑って行こうとする福原、戻って。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '福原', 'ああ。(小指を立て)コレとうまくやってるか、ええ？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (28, 'dialogue', '一也', 'え？　ハハ(愛想笑いで)');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'description', NULL, '新聞を読みながらコーヒーを飲んでいる仲村。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'description', NULL, '一也が入ってくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '七海', 'いらっしゃい');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'description', NULL, '一也、仲村に軽く会釈する。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'description', NULL, '仲村も軽く返す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '一也', 'ソーキカレーください');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '七海', 'はーい');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '一也', '（小さな声で七海に）あの、ヤナワラバーってどういう意味ですか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '七海', 'ヤナワラバー？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'description', NULL, '声が大きい、という顔の一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '仲村', '（新聞に目を落としたまま）それはフジのことだな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '一也', '（え？と仲村を振り返る）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'description', NULL, 'クスクス笑う七海。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '一也', '（さっぱりわけがわからず）……？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '七海', '腕白で、強情っ張りって意味');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'description', NULL, '新聞で顔を隠し、笑っている仲村。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '七海', '(笑いながら)あ、ソーキカレーね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (29, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (31, 'description', NULL, '慣れた手つきで、魚を処理していく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (31, 'description', NULL, 'ポロシャツの背の『ＳＴＡＦＦ』の文字が、ボロボロになっている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (32, 'description', NULL, 'ユリと剛、飼育員たちがイルカを押さえている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (32, 'description', NULL, '手際よく採血する一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (32, 'description', NULL, 'イルカは、信頼しきった様子で静かに体を預けている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (32, 'dialogue', 'ユリ', 'チャオの朝の数値どうだった？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (32, 'dialogue', '一也', '白血球の数も下がってきてますし、血液像も良化してるんで、このままでいきますい');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (33, 'description', NULL, '検査機器を使いこなす一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (33, 'description', NULL, '棚には日誌がズラリと並んでいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'description', NULL, '仕事が一息ついた一也、携帯で陽子と話している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'dialogue', '一也', 'もしもし？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'dialogue', '陽子の声', 'どうしたの？　こんな時間にめずらしいね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'dialogue', '一也', 'いや、ちょっと時間空いたから。今大丈夫？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'description', NULL, '陽子の声｢うん。大丈夫だよ。今、成田に向かってるところ｣');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'dialogue', '一也', 'え、また出張？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'dialogue', '陽子の声', 'そう、今週も休みなし。大変。一也はどうしてんの？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'dialogue', '一也', 'こっちも大変だよ。やんなきゃいけないことめちゃくちゃいっぱいあってさ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'dialogue', '陽子の声', 'へー');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'dialogue', '一也', '獣医オレ一人だし');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'dialogue', '陽子の声', '頑張ってるじゃん');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'description', NULL, '楽しく話している一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (34, 'description', NULL, '丸窓の向こうから、フジがそんな一也を見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (35, 'description', NULL, '三線をつま弾く仲村。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (35, 'description', NULL, '七海、そばの椅子に座り、その美しい音色を聞いている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (36, 'description', NULL, '丸窓の前、ホイッスル音を出し、落ち着かない様子のフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (36, 'description', NULL, 'どこか不安そうだ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'description', NULL, '一也がラグーンプールの階段を駆け上り、急いで梯子を降りていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'description', NULL, 'プールの中央にはフジを囲むように仲村、剛、ユリ、飼育員Ａ、Ｂ、Ｃがいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'description', NULL, '一也、歩み寄り、');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'dialogue', '一也', '！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'description', NULL, 'フジの尾びれの端が白く変色している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'description', NULL, '仲村に促されて、尾びれの匂いを嗅ぐ一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'dialogue', '一也', '（ウッと顔をしかめる）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'dialogue', '仲村', '腐った匂いがするだろ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'dialogue', '一也', '……壊死してますね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'dialogue', '剛', '壊死してるのはわかってるさッ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'dialogue', '剛', 'お前、獣医のくせに気づかなかったのかよ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'description', NULL, '一也に掴みかかる剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'description', NULL, '一也、その手を振り払うが、剛は再び掴みかかる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'description', NULL, '２人、揉み合いになる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'description', NULL, '止めようとする仲村の声も聞かず、掴み合ったまま水の中に転がる２人。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (37, 'dialogue', '仲村', 'いい加減にしろッ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'description', NULL, 'ずぶ濡れの一也、入ってくるなりドクターシャツを脱ぎ、床に叩きつける。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'description', NULL, 'ユリが合羽を取りに入ってくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'description', NULL, '部屋に気まずい空気が流れる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'dialogue', 'ユリ', '……比嘉君ね、前に担当してたリュウっていうイルカを亡くしてるの');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'dialogue', 'ユリ', 'だから獣医のあなたには、ついキツイこと言っちゃうのよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'dialogue', '一也', '気持ちはわかりますけど……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'dialogue', 'ユリ', '植村君');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'dialogue', 'ユリ', 'あなた、自分が世話してきた動物、死なせたことある？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'description', NULL, '記録用のビデオカメラを手に、プールに戻るユリ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (38, 'description', NULL, '部屋に一人残された一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (39, 'description', NULL, 'プールサイドにミチルがたたずみ、じっとフジを見つめている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (39, 'description', NULL, '弱々しい泳ぎのフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (39, 'dialogue', '一也Ｎ', 'フジの発病は突然だった……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (40, 'description', NULL, '日誌を取り出す一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (40, 'dialogue', '一也Ｎ', '原因不明の感染症。壊死がなぜ起こったのか？　わからない……。野生のイルカにも、同じ症例が確認されている');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (40, 'description', NULL, '『白血球』『血沈』『壊死』と、文字が綴られていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'description', NULL, '水を落としたプール。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'description', NULL, 'ドクターシャツを着た一也が、フジの尾びれの壊死した部分を消毒している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'description', NULL, '剛、フジの尾びれを押さえている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'description', NULL, '目線を合わすことなく作業している２人。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'description', NULL, '餌の魚に抗生薬を埋め込む一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'dialogue', '一也Ｎ', '薬をより強いものに変える。その副作用なのか、肝機能障害が出始める。しかし、その後も壊死は止まらず、進行する');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'description', NULL, 'フジの口を開けようとする剛とユリ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'description', NULL, '一也が魚を与えようとするが、フジは口を開かない。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'description', NULL, '一也、剛、ユリ、フジに点滴をしている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (41, 'dialogue', '一也Ｎ', '栄養と薬を与えるため、点滴に変える。しかし、その後も壊死は止まらず、進行する');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'description', NULL, '仲村のデスクの前に一也がいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'dialogue', '一也', '調べた限りでは、フジと似たような症例で、生存しているイルカは１頭もいません');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'description', NULL, '自席で仕事しながら、一也の言葉を聞いている剛、ユリ、飼育員Ａ、Ｂ、Ｃ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'dialogue', '仲村', '……で、お前の結論は？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'dialogue', '一也', '……壊死した部分を、切り落とすしかないと思っています');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'dialogue', '剛', '!?');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'dialogue', 'ユリ', '他に方法はないの？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'dialogue', '一也', 'このまま今の治療を続けても、死ぬのを待つだけです');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'dialogue', '一同', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'dialogue', '剛', '切れば助かるのか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'dialogue', '一也', '壊死が止まらなければフジは確実に死にます。切り落として壊死が止まれば、助かる可能性があります');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (42, 'dialogue', '一同', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (43, 'description', NULL, '海側にもう１つ小さなプール（隔離プール）がある。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (43, 'description', NULL, 'フジの尾びれを、サーモグラフィーのカメラで診察する一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (43, 'dialogue', '一也Ｎ', 'サーモグラフィーで壊死の状態を検査する');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (43, 'description', NULL, '色が異なる尾びれの画像が現れる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (43, 'dialogue', '一也Ｎ', 'フジの尾びれの４分の３に血液が流れていなかった');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (44, 'description', NULL, 'フジがポツンと一頭だけ弱々しく浮かんでいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (44, 'dialogue', '仲村の声', '明日、電気メスによるフジの尾びれの切除手術を行う');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (45, 'description', NULL, '仲村が、一也と飼育員全員を前に話している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (45, 'description', NULL, '神妙な様子で聞いている一同。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (45, 'dialogue', '仲村', '手術は県立北部病院の島袋先生にお願いしました。尾びれの右側を島袋先生、左側を植村が担当する');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (45, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (45, 'dialogue', '剛', '経験もない獣医で大丈夫なんですか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (45, 'dialogue', '仲村', '誰にでも初めてはある');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (45, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (46, 'description', NULL, '福原が、浮かんでいるだけのフジを見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (46, 'description', NULL, 'やってきた一也、福原に気づく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (46, 'dialogue', '一也', 'お疲れさまです');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (46, 'dialogue', '福原', '……単純だけどいい名前だよな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (46, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (46, 'dialogue', '福原', '富士山の見える伊豆の海から来たからフジ。オレが付けたんだけどな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (46, 'dialogue', '一也', '（微苦笑）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (46, 'dialogue', '福原', 'もう30年になるか、フジがここに来てから……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '沖縄海洋博覧会の会場(空撮)。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '――テロップ『１９７５年　沖縄国際海洋博覧会』');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'dialogue', '福原の声', '沖縄海洋博が終わってすぐだったから……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '会場へリポート。木箱に入れられた、およそ６歳の雌イルカ（フジ）が、ヘリコプターから搬出される。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '水族館。専用担架で吊るされ、背中には濡れ毛布をかけられたフジがプールに放たれる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'dialogue', '福原の声', 'フジはとにかく頑固で、あまのじゃくで、手を焼かされたもんだ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '沖縄海洋博覧会でのイルカショー。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'dialogue', '福原の声', 'ショーが嫌いでな、得意な技は１つもなかった');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, 'フジの出産。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '生殖溝から出てくる赤ん坊の尾びれ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'dialogue', '福原の声', 'だけど、３頭も子供を生んで、育てた。フジは美ら海のビックマザーだ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (47, 'description', NULL, '子供をプールの縁から守るように並んで泳ぐフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (48, 'dialogue', '福原', '……手術は明日か');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (48, 'dialogue', '一也', 'はい……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (48, 'dialogue', '福原', 'そうか……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (48, 'description', NULL, '去っていく福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (48, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (48, 'description', NULL, '静かに浮かんでいるフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'description', NULL, 'ロードバイクで帰ってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'description', NULL, 'と、家の前でミチルがしゃがんで待っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'dialogue', 'ミチル', '（じっと見つめ）……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'description', NULL, '一也、通り過ぎようとすると、ミチルが立ち上がる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'dialogue', 'ミチル', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'dialogue', '一也', '(立ち止まり)……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'dialogue', 'ミチル', '……フジは助かるのか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'dialogue', '一也', '……やれるだけのことはやる');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'description', NULL, '一也をじっと見て、踵を返し帰っていくミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (49, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (50, 'description', NULL, '迷路のような道を歩いていくミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (51, 'description', NULL, '担架に乗せられたフジが、剛、ユリ、飼育員Ａ、Ｂ、Ｃに押さえられている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (51, 'description', NULL, '仲村と一也の前に、県立北部病院の医師・島袋隆史（57）。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (51, 'description', NULL, '一也がフジの尾びれに麻酔を打つ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (51, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (51, 'description', NULL, '電気メスを手にした島袋が、右の尾びれを切り落としていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (51, 'description', NULL, 'その手の動きをじっと見ている一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (51, 'description', NULL, 'フジを押さえる剛の手に力が入る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (51, 'dialogue', '剛', '頑張れよ、すぐ終わるから');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (52, 'description', NULL, '息を切らしながら険しい坂を上っていくミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (53, 'description', NULL, '電気メスの鋭い音が微かに響いている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (53, 'description', NULL, 'じっと痛みに耐えているフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (53, 'description', NULL, '心配そうに見守る仲村、剛、ユリたち。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (53, 'description', NULL, '左の尾びれが切り離され、受け取る一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (53, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (54, 'description', NULL, 'ミチルの前に、がじゅまるの巨木が現れる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (55, 'description', NULL, '一也、大きく深呼吸すると、左の尾びれを電気メスで切除し始める。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (56, 'description', NULL, '森の中にひっそりそびえるがじゅまるの木。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (56, 'description', NULL, '木の下に歩いていくミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (57, 'description', NULL, '真剣な表情の一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (57, 'description', NULL, 'フジを押さえる剛、一也の手術を見守っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (57, 'description', NULL, '分厚い尾びれから煙が出る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (57, 'description', NULL, 'フジの鳴き声が周囲を包み込む。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (58, 'description', NULL, '木の根元で、手を合わせ祈るミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (59, 'description', NULL, 'うちわのように丸く小さくなった尾びれのフジに、剛が餌を与えている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (59, 'description', NULL, '餌を食べるフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (59, 'dialogue', '一也Ｎ', '手術は成功した。フジの食事量は少しずつ増え、傷口も順調に回復してきている');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (60, 'description', NULL, '飼育員総動員でフジを移動している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (60, 'description', NULL, '落水されたプールから、専用担架に乗せられたフジがクレーンで吊り上げられ、プールの外に止められたトラックの荷台に移される。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (60, 'dialogue', '一也Ｎ', '白血球の値が正常に戻り、２次感染の心配もなくなった');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (60, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (60, 'description', NULL, '走るトラック。その荷台、専用担架に乗ったフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (60, 'dialogue', '一也Ｎ', 'フジを隔離プールからラグーンプールに戻す。フジの命は、助かった');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (60, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (60, 'description', NULL, 'クレーンに吊るされた担架からメインプールに放たれるフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (61, 'description', NULL, 'ほとんど動くことなく浮かんでいるだけのフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (61, 'description', NULL, '土砂降りのプールサイドで、フジを見つめるミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (62, 'description', NULL, 'いくつものホイッスル音を発しながら、野性のイルカたちが、自由に大海を泳いでいく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (63, 'description', NULL, 'ハッと夢から覚める一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (63, 'dialogue', '一也', '（あ……）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (63, 'description', NULL, '倒れたカップからコーヒーがこぼれ、本の表紙がコーヒーまみれになっている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (63, 'description', NULL, '慌てて拭く一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (63, 'description', NULL, 'その本、『マナティ、海に暮らす』。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (63, 'description', NULL, 'ふと、イルカラグーンの方に人の気配を感じ、窓の外を見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (64, 'description', NULL, 'プールの中央に、フジがポツンと浮かんでいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (64, 'description', NULL, 'プールサイドに立つ人影が、突然プールに飛び込む。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (64, 'description', NULL, '丸窓の中を横切る人影。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (64, 'description', NULL, '懐中電灯を手に階段を上がってきた一也、あたりを見回す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (64, 'description', NULL, 'プールから上がった人影が走り出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (64, 'description', NULL, '後を追う一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'description', NULL, '逃げる人影。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'description', NULL, '追う一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'description', NULL, '一也、人影に追いつき、その背を掴む。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'description', NULL, '倒れ込む２人。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'description', NULL, 'くんづほぐれつ相手を押さえ込む一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'dialogue', '一也', 'おいッ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'description', NULL, '相手の肩を掴み、顔を見る一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'dialogue', '一也', '（お前）……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'dialogue', 'ミチル', '（キッと一也を睨み）……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'dialogue', 'ミチル', '(その手を振り払い)泳げないイルカはイルカじゃない！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'description', NULL, '暗闇の向こうへ走り去っていくミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (65, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (66, 'description', NULL, 'ポツンと浮かんでいるフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (66, 'description', NULL, 'じっと見つめている一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (66, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (67, 'description', NULL, 'サバニの船底に溜まった水を、アカ汲みでかきだしているオジイ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (67, 'description', NULL, 'ロードバイクを押して、一也が力なく歩いてくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (67, 'dialogue', 'オジイ', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (67, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (68, 'description', NULL, 'オジイと一也を乗せたサバニが、沖へ向かって走る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (68, 'description', NULL, 'やがて、大海原が広がっていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (68, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (68, 'description', NULL, '夕凪になっている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (68, 'description', NULL, 'サバニの上のオジイと一也を、夕陽がオレンジ色に染めている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (68, 'description', NULL, '身じろぎもせず、海を見つめているオジイと一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (68, 'description', NULL, '69　海沿いの道');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (68, 'description', NULL, '一也が全速力でロードバイクをこいでいく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (70, 'description', NULL, '机の上の資料の山から『マナティ、海に暮らす』を探し出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (70, 'description', NULL, '傍にあった『ＣＲＣハンドブック』も手に取る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (70, 'dialogue', '一也の声', 'フジは、このまま泳がない状態が続けば、新たな病気を引き起こす可能性があります。フジの尾びれを再生するプロジェクトを立ち上げたいんです――');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'description', NULL, '海を一望できるベランダに立つ福原と一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'description', NULL, '福原は望遠鏡でホエールウォッチングをしている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '一也', '――直接、ブリヂストンの本社に交渉しに行きたいのですが');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '福原', '……面白そうじゃないか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '福原', 'フジが泳げるようになれば、ミチルも元気が出るだろう');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '一也', '？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '福原', 'ミチルの母親は男を追いかけて本土に行ったんだ。それ以来、オジイがあの子の面倒を見てる');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '福原', 'ミチルは、フジに母親を重ねてんのかもしんねえなぁ……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '福原', 'やってみな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '一也', '（嬉しい）……はい');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (71, 'dialogue', '福原', 'なかなか出くわさねえもんだなぁ、クジラには……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (72, 'description', NULL, 'オジイが縁側で煙草をふかしていると、ミチルが帰ってくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (72, 'dialogue', 'オジイ', 'ミチル、お前の母さんから、荷物届いてるよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (72, 'description', NULL, 'ミチル、宅配便の箱を一瞥するが、奥へいってしまう。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (72, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (72, 'description', NULL, '寝ているオジイの隣の布団で天井を見つめているミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (72, 'dialogue', 'ミチル', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (73, 'description', NULL, 'メモを見ながらやってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (73, 'description', NULL, '期待と不安の目でビルを見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, 'ブリヂストンの社員３人を前に、一也がいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, '社員たち、手元の様々なフジの写真に目をやっている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, 'ブランド推進室長、遠藤（５１）が、おもむろに口を開く。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'dialogue', '遠藤', '私どもは、自動車のタイヤなどのゴム製品を取り扱うメーカーです。正直……生き物相手の製品をこれまで作ったことがなく、非常に難しいかと……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, '開発部部長の須藤勇治（４１）と部下の原田（３２）もじっと聞いている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'dialogue', '一也', 'イルカの尾びれは、タイヤのゴムの感触にそっくりなんです');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, '『マナティ、海に暮らす』を取り出す一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, '表紙にはコーヒーの大きな染みができている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, '本を開き、海亀の人工手びれをアメリカのグッドイヤーが作成した写真を見せ、');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'dialogue', '一也', 'アメリカのグッドイヤーが、サメに右手を食べられた海亀に、手びれを作った例があります。イルカの人工尾びれはまだ誰も作ったことがなく、もしできれば、世界初です');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, 'じっと写真に見入っていた須藤、一也を見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'dialogue', '須藤', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, '一也、フジの写真を見せ、');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'dialogue', '一也', '今のままではフジは泳ぐことができません。浮いているだけです。毎日毎日、ただ浮いているだけなんです。フジに人工尾びれを作ってやってください。フジを助けたいんです。もう１度泳がせてやりたいんです。お願いします！（頭を下げる）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, 'じっと聞いている須藤。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'dialogue', '須藤', '……お話はよくわかりました。１度検討させてください。後日こちらから連絡差し上げます');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, '遠藤と原田、意外そうな顔で須藤を見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, '深く頭を下げたままの一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, '75　イルカラグーン・メインプール(夜)');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (74, 'description', NULL, '暗くなったプールに、静かに浮かんでいるフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (76, 'description', NULL, '窓の外は雨。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (76, 'description', NULL, '着メロが鳴る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (76, 'description', NULL, '青山陽子(24)が、携帯に出る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (76, 'dialogue', '陽子', 'もしもし');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (76, 'dialogue', '一也の声', 'もしもし、悪い、今そっち向かってるから');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (76, 'dialogue', '陽子', '後どれくらいかかりそう？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'description', NULL, '雨の中、携帯で話しながら歩いている一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', 'んー、３０分ぐらいかな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', '３０分？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'description', NULL, 'カフェで一也を待っている陽子、携帯で話す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'description', NULL, '※以下、一也と陽子のシーン、カットバック。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', 'うん');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', '時間ないよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', 'えっ、なんで？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', '会社戻んなきゃいけないの');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', 'マジ？　こんな時間に？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', '仕事残ってるから');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', '(立ち止まり)……会いたい');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', '……私も同じだよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', 'ずっと待ってたんだから');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', '……ゴメン');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', 'またさ、東京来る予定ないの？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', '……いや、ちょっと、わかんないな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', 'そっか。タイミング合わないね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', '“どこでもドア“でもあればいいのにな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', '変わらないねー、一也は');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', 'じゃあ……気をつけて帰ってね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '一也', 'うん……じゃあ、また連絡する');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'dialogue', '陽子', 'うん、バイバイ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'description', NULL, '電話を切る陽子。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (77, 'description', NULL, '一瞬フッと寂しげな顔で、水滴が流れる窓ガラスを見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (78, 'description', NULL, 'マナティーがゆったりと水の中を泳いでいる館内。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (78, 'description', NULL, 'ミチルが、モニター画面を見つめている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (78, 'description', NULL, 'マナティーの出産シーンの映像が映し出されている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (78, 'description', NULL, '生殖溝から赤ん坊のマナティーが姿を現わし、水中に生まれ出る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (78, 'description', NULL, '小さな赤ん坊のマナティーは、呼吸をしようと自力で必死に水面に向かう。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (78, 'dialogue', 'ミチル', '（じっと見入って）……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'description', NULL, '窓際の席で、煙草を燻らせている仲村。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'description', NULL, 'カウンターで、剛とユリがソーキカレーを食べている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '剛', 'たった１頭のイルカのために、大企業がそんなもん作ってくれるはずないと思うんだよね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', 'ユリ', 'わかんないわよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '七海', '植村君だけにね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '剛', '何だよそれ、七海さんまで');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'description', NULL, 'そこへ入ってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '七海', 'いらっしゃい');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', 'ユリ', 'ブリヂストンから連絡来た？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '一也', '（力なく首を振る）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '剛', 'もう忘れてんじゃないのぉ？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '一也', '……ソーキカレー大盛り！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '七海', 'はーい');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '剛', '大体さ、イルカは体に異物をつけられるの、すごく嫌がるんだよ。うまくいくとは思えないね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '一也', 'これからは治療だけじゃなくて、リハビリも必要になってくると思うんですよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '剛', 'それはお前の勝手な理屈だろ。そんなことフジがホントに望んでんのか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '一也', '望んでるかもしれないじゃないですか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '剛', '望んでないかもしれないだろ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '一也', 'わかんないでしょ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '剛', 'わかんないだろ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'description', NULL, '七海、場の空気を和ますように、冗談めかせて、');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '七海', '……フジがしゃべれたらわかるのにねぇ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'description', NULL, '一同、七海を見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '仲村', '毎日顔つき合わせてたってわからないもんなんだよなぁ、イルカの気持ちって');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '七海', '人の気持ちとおんなじだ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'description', NULL, '仲村、七海を見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'description', NULL, 'フッと微笑む七海。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'dialogue', '仲村', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (79, 'description', NULL, '仲村の座っていた窓際の席で、一人夜の海を眺めている七海。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (80, 'description', NULL, '飼育員たちが、餌をやりながらトレーニングをしている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (80, 'dialogue', '一也の声', '課長！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (80, 'description', NULL, '振り返る仲村。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (80, 'description', NULL, '一也が、海獣課の窓から身を乗り出している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (80, 'dialogue', '一也', '連絡が来ました！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (80, 'description', NULL, '一同、驚いて一也を見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (80, 'dialogue', '剛', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (80, 'dialogue', '一也', 'ブリヂストンが来ます！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (81, 'description', NULL, '一也のパソコンに、須藤からのメールが届いている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (81, 'description', NULL, '『フジがもう一度元気に泳げるようになるために、是非協力させて頂きたいと思っております。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (81, 'description', NULL, '株式会社ブリジストン　化工品材料開発部　須藤勇治』');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (82, 'description', NULL, 'ただ浮いてるだけのフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (82, 'description', NULL, 'その周りを気持ちよさそうに泳いでいるイルカたち。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (83, 'description', NULL, '入ってくる一也、出てきた剛とすれ違う。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (83, 'dialogue', '剛', '……植村');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (83, 'dialogue', '一也', 'はい？(と振り返る）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (83, 'dialogue', '剛', 'フジのためになることだったらオレは何でもやる。でも、少しでもフジを傷つけたり、ストレスを与えるようなことがあったら一切協力しないからな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (83, 'description', NULL, '去っていく剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (83, 'description', NULL, '立ち止まったまま、その背を見ている一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (84, 'description', NULL, 'ついに、人工尾びれプロジェクトが始動！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (84, 'description', NULL, '福原が仲村、一也、剛、ユリ、飼育員全員を前に話している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (84, 'dialogue', '福原', 'フジの命は助けることができた。でも、本当にそれだけでいいのか？　俺たちには何ができるのか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, '落水したプール。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, '一也が、フジのうちわのような尾びれを須藤に触らせる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, '興味深く、その感触をたしかめている須藤。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, 'その光景に、福原の声がかぶる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'dialogue', '福原の声', 'イルカを水族館なんかで飼ってるから、プールに閉じ込めて、冷凍の魚を食わしてるから病気になるんだ、そう言う人もいるだろう。まあ、そうかも知れない。そう言われても仕方がない');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, '福原の声が、フジの尾びれ型取り作業にかぶる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, '剛、ユリ、飼育員全員で粘土状のパテをこねている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'dialogue', '福原の声', 'でもな、水族館で海の生物のことを知りたい、見たいという人がいる以上、俺たちはできる限り、生き物にとって快適な環境を作らなきゃならない。人工尾びれなんてもんは、フジにとっちゃいい迷惑なのかも知れねえな。『こんなもんつけやがって泳ぎにくくてしょうがない』そう思うかも知れねえよ。イルカにとって何が幸せなのか、俺たち人間にわかるはずがねえんだ。今回のことはこれからの治療につなげるつもりでやろう。泳ぎが確実に良くなった、速さがこれだけ違うというきちんとしたデータを残してくれ。『人工尾びれを作りました。素晴らしい』そんな、人の勝手な思い込みで終わらせちゃいけねえ。それが、世界初ということの意義であり、使命だと思う');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, '一也が木箱を押さえ、須藤が中心となり、そこにこねたパテを詰めていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, '暴れるフジの体を飼育員たちが押さえ、ユリがホースの水をかける。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, '仲村と剛が、パテの詰まった木箱の上にフジの尾びれを乗せる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (85, 'description', NULL, '上蓋を強く押すようにかぶせる須藤と一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (86, 'description', NULL, '一也がパソコンに向かい、キーを叩いている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (86, 'dialogue', '一也の声', 'ブリヂストン須藤様　リクエストが２点あります。１つは、残ったフジの尾びれを傷つけないこと');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (87, 'description', NULL, 'フジの人工尾びれの型に、液体状のゴムを流し込んでいく須藤。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (87, 'dialogue', '一也の声', '２つ目は壊れないこと。プールの中で壊れると、その破片をイルカが食べてしまう危険があるからです');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (88, 'dialogue', '一也の声', '追伸　人工尾びれの完成を待つ間、フジの異物装着訓練を始めました');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (88, 'description', NULL, '落水されたプール。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (88, 'description', NULL, 'くねくねと逃げていくフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (88, 'description', NULL, '黄色い布を手にした剛とユリが、フジの尾びれに巻きつけようと追いかけている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (88, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (88, 'description', NULL, '水の張られたプール。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (88, 'description', NULL, '尾びれにふんどし型ベルトを巻き、平然と浮いているフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (88, 'description', NULL, '目を見張っている仲村。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (88, 'dialogue', '剛', '（満足気に）もう、何つけられても大丈夫ですよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (88, 'description', NULL, '一也もユリも、プロジェクトの前進を感じてか、表情が明るい。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (89, 'description', NULL, '夢中でロードバイクを走らせる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (89, 'description', NULL, 'と、道の先に、マウンテンバイクに乗ったミチルの後ろ姿が見えてくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (89, 'description', NULL, '一也、追い越しざま、');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (89, 'dialogue', '一也', 'イルカの尾びれって、また生えてくるって知ってた？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (89, 'dialogue', 'ミチル', 'え？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (89, 'description', NULL, '思わず笑いながら、走り去っていく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (89, 'dialogue', 'ミチル', '？？？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (90, 'description', NULL, '机の上に、届いたばかりのダンボール箱を置く一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (90, 'dialogue', '一也の', '開けますよー！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (90, 'description', NULL, '一也がふたを開けると、中には黒いゴム製の人工尾びれ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (90, 'description', NULL, '目を輝かせて、大切に人工尾びれを取り出す一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '落水されたプール。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '須藤の指示の下、剛がフジの尾びれを持ち、一也が第１次試作の人工尾びれを装着する。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '仲村、ユリ、飼育員たちが見守っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, 'みんなでフジを専用担架から放すと、給水口から海水が注ぎ込まれる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, 'プールサイドから様子を見ているミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, 'フジを見つめるミチルに気づく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '海水が満ちたプール。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '飼育員が水中カメラをスタンバイする。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, 'フジが人工尾びれを上下に振り始める！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'dialogue', '一也', '?!');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '人工尾びれを懸命に振り、水中へと潜っていくフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '「泳いでる！」「尾びれを振ってる！」「やった！」');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '飼育員たちから歓声が上がる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '剛の顔にも満足そうな笑みが広がる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '一也と須藤、丸窓へ走る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '他のイルカたちとともに水中を泳いでいくフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (91, 'description', NULL, '丸窓から、フジをビデオカメラで撮影する一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'description', NULL, 'モニター画面に、先ほどのフジの泳ぎが映し出されている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'description', NULL, '須藤と海獣課の面々、感慨深げに眺めている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '仲村', 'いやぁ、久しぶりに泳いでるフジを見ましたよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '須藤', '実は内心ヒヤヒヤしてたんです');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '仲村', '？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '須藤', '人工尾びれが重くてフジが沈むんじゃないかって');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'description', NULL, '一同、微笑む。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'description', NULL, 'フジがキックすると、気泡が多く立ち上がる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'description', NULL, 'なかなか進まない。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '一也', 'かなり水の抵抗がかかるんですかねぇ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '須藤', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '一也', '普通、尾びれを１、２度振るとスイッと進みますが、フジは何度もキックしてますよね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'description', NULL, '何度もバタバタとキックし、泳いでいくフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '須藤', '次は尾びれの大きさと装着部分を改善していきましょう');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '一也', 'はい');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'description', NULL, '画面には、コニーの泳ぎが映し出されている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'description', NULL, 'コニーの尾びれの動きに、気泡は全くない。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', 'ユリ', 'コニーの尾びれってホント綺麗……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '須藤', '機能的に優れたものは形も美しいですよね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'description', NULL, '須藤も見とれている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '須藤', 'やっぱり本物はすごいな……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '剛', '（仲村に）リュウの尾びれ、使えるんじゃないですか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'dialogue', '仲村', '（須藤に）死んだイルカの尾びれならあるんですが、参考になりますか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (92, 'description', NULL, '須藤、仲村を見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (93, 'description', NULL, 'ホルマリン漬けのイルカの尾びれが、３Ｄ撮影されている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (93, 'description', NULL, 'パソコンを操作する須藤。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (93, 'description', NULL, '画面に、尾びれの３Ｄ画像と数字や記号が現われる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (94, 'description', NULL, '丸窓に頬ずりするように近づいてくるフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (94, 'description', NULL, 'ミチルが丸窓に顔を押し当て、泳ぐフジを嬉しそうに見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (94, 'dialogue', '一也の声', '須藤様　フジは人工尾びれを着けたことで、少しずつ泳ぎを思い出したようです。最近は、人工尾びれを着けなくても泳ごうとしています。フジの、いや、生き物の生命力には本当に驚かされます');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'description', NULL, '剛が膝下まで水に浸かり、ターゲットをメインプールにいるフジの口に近づける。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'description', NULL, 'しかし、フジは敷居を乗り越えようとしない。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'description', NULL, 'プールサイドにやってきた一也、その様子を見つめる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'description', NULL, '何度も何度もターゲットを掲げ、フジを呼ぶ剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'description', NULL, '柵外からフジを見つめるミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'dialogue', 'ユリ', '(ミチルを見て)尾びれを安全に着けるために、浅瀬プールに呼び込む訓練なの');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'dialogue', 'ミチル', '（黙ってフジを見つめたまま）……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'dialogue', 'ユリ', 'フジも入ったことのない場所は怖いんだろうな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'description', NULL, '再びターゲットをフジに向ける剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'description', NULL, 'しかし、フジは入ってこない。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (95, 'dialogue', 'ミチル', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (96, 'description', NULL, '雨の中、剛が一人、ターゲットをフジに向け訓練している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (96, 'description', NULL, '敷居の前で、しばらくターゲットを見つめているフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (96, 'description', NULL, 'しかし、くるりと向きを変え、いってしまう。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, '剛が餌のバケツを持って浅瀬プールに入ってくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, 'プールの中にダイビングベルを入れて鳴らし、メインプールで漂うフジを呼ぶ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, 'ターゲットを見せ、フジを浅瀬に呼び込もうとする剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, 'しかし、寄って来るものの、フジはしばらくするとまた戻ってしまい、メインプールで漂うだけだ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, 'プールサイドで見つめているミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, '何度もターゲットをフジに向ける剛。額から汗がしたたり落ちる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, '休んでいる剛。その足元で漂うフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, '再び、フジにターゲットを向ける剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, 'と、フジが初めて敷居の上に体を乗り上げる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, 'しかし、あと一歩のところで、メインプールに戻ってしまう。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, '真剣な眼差しで剛とフジを見つめるミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, '剛のサインに応え、フジが再び敷居に乗り上げる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, '一瞬時間が止まったように、敷居の上で止まったままのフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, 'ターゲットを掲げる剛の腕に力が入る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, 'ついに、滑るようにフジが浅瀬へ飛び込んでくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'dialogue', 'ミチル', '!?');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, 'ホイッスルを鳴らし、餌をあげ、フジを撫でる剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'dialogue', '剛', 'よぉし、やったぞ！　やったぞ！　フジ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (97, 'description', NULL, '剛と同じ気持ちでフジを見つめるミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (98, 'description', NULL, '机の上に、初期型とクロスベルト型、２つの人工尾びれが並べて置かれている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (98, 'description', NULL, 'その周りに、須藤、仲村、一也、剛、ユリ、飼育員たちが集まっている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (98, 'description', NULL, '机の上にカウリング型を置く須藤。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (98, 'description', NULL, '試作品に注目する一同。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (98, 'dialogue', '須藤', 'このカウリング型は、クロスベルト型と比べて水の抵抗はほぼ半分になりました');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, '剛のサインで敷居を乗り越えてくるフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, 'ユリが尾びれを押さえ、剛がカウリング型人工尾びれを装着する。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, '一也と須藤、仲村が作業を見守る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, 'フジが、ドルフィンキックで泳ぎ始める。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, 'フジの泳ぎを見守る一同。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, 'ミチルも柵外からじっと見つめている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, 'フジが、他のイルカたちと戯れるように泳いでいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, '思わず水中マスクをつけ、プールの中へ入る一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, 'キックする人工尾びれから泡はほとんど出ていない。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, 'スピードをあげて、グングン泳いでいくフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, '水の中から顔を出す一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'dialogue', '一也', '（笑顔で）どれがフジだかわかりません！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, '見ていた一同に笑顔が浮かぶ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (99, 'description', NULL, 'フジが、水中から顔を出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', 'ユリ', '偉大なるビッグマザー、フジに乾杯！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '一同', '乾杯！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'description', NULL, 'すでに宴もたけなわである。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'description', NULL, 'テーブルの上には、たくさんの沖縄料理。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'description', NULL, 'そして、いくつもの空になったグラス。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'description', NULL, '仲村、剛、ユリら飼育員たち。一也、須藤。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'description', NULL, '七海が須藤に泡盛を勧める。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', 'ユリ', '館長のおごりですから、どんどん飲んでください');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '須藤', '（飲んで）うまい！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'description', NULL, '一同に笑いが広がる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '須藤', '実は禁酒してたんですよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '一也', 'え、そうなんですか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '須藤', '人工尾びれが軌道に乗るまで、飲まないって決めてたんです');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '剛', '浮いてるだけだったフジが、あんなに生き生きと泳ぐなんて、俺はもうサイコーだッ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'description', NULL, 'ほろ酔いの剛、嬉しそうだ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '須藤', 'イルカの尾びれを作ってるって言ったら、初めて娘が誉めてくれましたよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '七海', 'フジさまさまですね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '須藤', '久しぶりに、モノづくりの面白さを思い出しました');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '仲村', '私もこの仕事を20年やっていますが、イルカに教えられることばっかりです');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'dialogue', '一也', '完全にオレたち、フジに先越されてますよね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (100, 'description', NULL, '盛り上がる宴は続く。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (101, 'description', NULL, '丸窓にもたれて眠ってしまっているミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (101, 'description', NULL, '安心したような、どこか穏やかな顔だ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (101, 'description', NULL, '丸窓から、フジがそんなミチルを見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (102, 'description', NULL, '縁側でごろ寝して泡盛を飲んでいる福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (102, 'description', NULL, 'オジイが家の奥から新しいアカ汲みを持ってきて、黙って福原の側に置く。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (102, 'dialogue', '福原', '(起き上がって)……もう海へは出ないのか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (102, 'dialogue', 'オジイ', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (102, 'description', NULL, '２人きりで静かな杯を交わしている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (103, 'description', NULL, '一也が、イルカにエコー検査を行っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (103, 'description', NULL, 'モニター画面に、イルカの胎児が映し出される。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (103, 'description', NULL, '見学の子供たち、熱心に一也の説明を聞きながら、検査を見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (103, 'dialogue', 'ユリの声', '植村君！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (103, 'dialogue', '一也', '(？と振り返る）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (103, 'description', NULL, 'メインプールで、ユリが興奮している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (103, 'dialogue', 'ユリ', 'フジがジャンプしたよ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (103, 'dialogue', '一也', '!?');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, 'ビデオカメラを手に、飼育員が水中に潜っていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'dialogue', '一也', '(やって来て)ユリさん、ホントですか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'dialogue', 'ユリ', 'コニーのターゲット目指してジャンプしてきたんだから！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'dialogue', '一也', '(嬉しそうにフジに)すごいな');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, '一也、仲村、飼育員たちが見守る中、剛がハイジャンプのサインを出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, '勢いをつけて水面を泳いでいくフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, '水面から、勢いよく体が出る！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, '瞬間、鈍い音が響き、水中に沈んでいくフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'dialogue', '一也', 'あれ？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'dialogue', 'ユリ', '割れた!?');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, '剛がプールに飛び込む。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, '心配そうに見つめる一也、仲村、ユリ、飼育員たち。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, '割れたカウリングを手に、剛が水中から顔を出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, 'フジに横たわりの姿勢をさせ、尾びれを押さえる剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, 'フジの尾びれを診る一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (104, 'description', NULL, '尾びれには血が滲み、深い傷がある。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', 'ユリの声', 'あー、ここです');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'description', NULL, 'モニター画面に、水中カメラからの人工尾びれの割れる瞬間が映し出されている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'description', NULL, '一也、飼育員たち、須藤が見つめている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'description', NULL, 'ライトボードには、人工尾びれのレントゲン写真。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'description', NULL, '机の上には、壊れた人工尾びれとその破片。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '須藤', 'まさかフジがジャンプするとは思いませんでした');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '仲村', '私たちも予想してませんでした');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '一也', '幸い怪我はたいしたことなかったです。それで、次回の改良ではもっと丈夫な――');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '剛', 'ちょっと待ってください');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'description', NULL, '一同、剛を見る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '剛', '人工尾びれをつけなくても、フジは充分泳げるようになってます。壊れるような人工尾びれはもう二度とつけたくありません');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '一也', 'でも、フジは飛ぼうとしたんですよ。応えてやるべきじゃないですか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '剛', '怪我したところから菌が入って、また壊死したらどうする?!');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '剛', 'お前、責任取れるのか?!');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '剛', 'どうなんだよ？　言ってみろ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'description', NULL, '一也に迫る剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '仲村', 'もういい。やめろ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '剛', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'description', NULL, '気まずい空気が流れる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'description', NULL, '「失礼します」と出て行ってしまう剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '仲村', '（須藤に頭を下げ）すみませんでした');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '須藤', '……いえ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (105, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (106, 'description', NULL, '暗くなったプールを、静かに泳いでいるフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (106, 'dialogue', '須藤の声', 'ちゅら海水族館　植村一也様――');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (107, 'description', NULL, 'デスクで、一也へのメール見つめている須藤。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (107, 'dialogue', '須藤の声', '――フジを助けたい、もう一度泳がせてやりたい。植村さんの言葉に動かされました。フジと出会えたことに感謝しています。ありがとうございました');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (108, 'description', NULL, 'デスクで日誌をつけている一也、その後姿。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (110, 'description', NULL, 'ユリがステージに登場しショーが始まる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (110, 'description', NULL, '客席の階段を陽子が下りてくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (110, 'description', NULL, 'ショーに出ている一也を見つけ、席に着く陽子。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (110, 'description', NULL, '一也に向かって、オキが口先で器用にボールを持ってくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (110, 'description', NULL, '観客から大きな拍手と歓声があがる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (110, 'description', NULL, 'ステージを掃除するイルカが口から出す水を逃げ回ってよける一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (110, 'description', NULL, 'ドッと受ける観客たち。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (110, 'description', NULL, 'そんな一也を見つめている陽子。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'description', NULL, '陽子が誰もいなくなった観客席に座っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'description', NULL, 'ジュース手にした一也が来る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '一也', 'シークヮーサージュース');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '陽子', 'ありがと');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '一也', '(腰を下ろし)……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '陽子', '……久しぶりだね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '一也', '……うん。来るなら連絡くれればよかったのに');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '陽子', '急に仕事が休みになったからさ。びっくりさせようと思って');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '一也', '（力なく笑って）……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '陽子', '……水族館の獣医ってさぁ、大変なんだね');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'description', NULL, '会話が弾まない２人。重苦しい空気。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '陽子', '……東京戻って来ないの？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'description', NULL, '一也、ちょっと話題を変えるように、');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (111, 'dialogue', '一也', '……あ、オレ明日休みだからさ、案内しようか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (112, 'description', NULL, '対岸の島に夕陽が沈んでいく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (113, 'description', NULL, '誰もいない――');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (113, 'description', NULL, 'クバの葉のアカ汲みに花が生けられている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (114, 'description', NULL, '墓に向かう人の列が続く。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (114, 'description', NULL, 'その中に一也もいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (114, 'description', NULL, '並んで歩くミチルに、話しかける福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (114, 'dialogue', '福原', '……オジイは、オジイのお母のところに帰ったんだ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (114, 'dialogue', 'ミチル', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (114, 'dialogue', '福原', '……ウチへおいで');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (114, 'dialogue', 'ミチル', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (114, 'dialogue', '福原', '婆さんと二人きりだ。心配ない');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (114, 'dialogue', 'ミチル', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (114, 'description', NULL, '答えず、ただ歩いていくミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (115, 'description', NULL, 'オジイのサバニが静かに浮かんでいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (116, 'description', NULL, 'がじゅまるの木に抱かれるようにして、静かに泣いているミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (116, 'description', NULL, 'その周りを、オオヤドカリが蠢いている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (117, 'description', NULL, 'さ迷うように歩いているミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (117, 'description', NULL, '涙に濡れた顔が、泥で汚れている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (117, 'description', NULL, '服にも、草や土がついている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (117, 'description', NULL, 'しかし、構わず、どこまでも歩いていくミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (118, 'description', NULL, 'ゆっくりとロードバイクをこいでくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (118, 'description', NULL, 'ふと、何かの気配を感じる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (118, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (119, 'description', NULL, '静かに浮いているフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (119, 'description', NULL, 'やがて、ゆっくりと水の中に潜っていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (120, 'description', NULL, '導かれるように浜辺の丘へやってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (120, 'description', NULL, '海上で、サバニが燃えている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (120, 'dialogue', '一也', '（不思議な感覚で見入って）……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (120, 'description', NULL, '下の浜辺で、ミチルが燃えるサバニを見つめている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (120, 'dialogue', 'ミチル', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (120, 'description', NULL, '浜辺に下りてきた一也、ミチルを見て、また海に目を戻す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (120, 'description', NULL, '静かに燃え落ちるサバ二を見つめている２人。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (121, 'description', NULL, 'やって来た一也が人工尾びれを掴む。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'description', NULL, '人工尾びれを手に、階段を上ってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'description', NULL, '飼育員たちが、餌をやりながらトレーニングしている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'description', NULL, 'その中の剛に近づいていく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '一也', '比嘉さん');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '剛', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '一也', 'この人工尾びれを見せて、フジが来なかったら諦めます');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '剛', '……(無視して歩き出す)');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '一也', '(後を追い)もし来たら、フジにはつける意思があるってことです。これをつけて泳ぎたいってことです。お願いします！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'description', NULL, '一也、剛の腕を取り');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '一也', '何かあったらオレが獣医として責任を取ります。やらせて下さい！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '剛', 'いい加減にしろよ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '一也', '（行く手を遮り）比嘉さん、つけるかつけないかはフジに決めさせて下さい！　お願いします！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'description', NULL, '頭を下げる一也を無視して行こうとする剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'description', NULL, 'と、その手を掴み、止める仲村。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '剛', '（見て）……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '仲村', '（剛に）……やってみろ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (122, 'dialogue', '剛', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'description', NULL, '剛が人工尾びれを胸の前で掲げ、メインプールにいるフジに向ける。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'description', NULL, 'ゆっくりと剛の前までやってくるフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'description', NULL, 'フジをじっと見つめる一也、仲村、ユリ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'description', NULL, 'フジはじっと剛を見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'dialogue', '剛', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'description', NULL, '人工尾びれをフジに突き出す剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'dialogue', '剛', '（思わず）来るなよ、フジ……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'description', NULL, '一也、仲村、ユリ、固唾を飲んで見守る……。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'description', NULL, '浅瀬へ飛び込んでくるフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'description', NULL, '剛の足元に静かに横たわり、尾びれを向ける。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'dialogue', '剛', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'dialogue', '一也', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'description', NULL, 'しゃがみ込んで、愛しそうにフジを撫でる剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (123, 'description', NULL, '一也が、あたたかな目でフジと剛を見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (124, 'description', NULL, '母親から送られてきた宅配便の箱を開けるミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (124, 'description', NULL, '中には洋服が入っている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (124, 'description', NULL, 'その一つを手に取ったミチル、広げてみると白地に花柄のワンピース。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (124, 'dialogue', 'ミチル', '(じっと見つめて)……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (125, 'description', NULL, '廊下をやってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (125, 'description', NULL, '須藤が部下に指導している。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (125, 'dialogue', '須藤', '（一也に気づき）……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (125, 'dialogue', '一也', '……(深く頭を下げる）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (125, 'description', NULL, '須藤、同じように頭を下げると、一也を見つめ、頷く。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (125, 'description', NULL, '一也の顔に笑みがこぼれる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (126, 'description', NULL, '隅にあるシートカバーを取る須藤。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (126, 'description', NULL, '歴代の人工尾びれがある。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (126, 'description', NULL, '須藤の顔に、新たな決意が広がる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (127, 'description', NULL, '母親が送ってきたワンピースを着たミチルが、柵越しにフジを見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (127, 'description', NULL, '柵の向こうで、フジもまたミチルを見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (127, 'description', NULL, '柵に沿って歩いていくミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (127, 'description', NULL, 'ミチルを追うように泳いでいくフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (127, 'description', NULL, '足を止め、じっとフジを見つめるミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (127, 'dialogue', 'ミチル', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (127, 'description', NULL, 'ミチル、思いを断ち切るように踵を返し、去っていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (127, 'description', NULL, 'ミチルの背中を見つめているフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'description', NULL, 'ソーキカレーを食べている福原の前に、仲村と七海が並んでいる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '仲村', '仲人はぜひ館長にお願いしたい、と２人の意見が一致しまして');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '七海', '引き受けてはもらえませんでしょうか？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'description', NULL, '頭を下げる２人。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '福原', '……繁殖だ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '二人', '？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '福原', 'いや、フジに負けないように、二人で励んで、繁殖だ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'description', NULL, '照れる仲村。微笑む七海。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'description', NULL, 'そこへ入ってくる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '福原', 'おう、お前知ってたか、これ？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '一也', 'えっ？　何ですか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '仲村', '館長（いいですから）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '福原', 'まったくなぁ……。あ、ミチル、東京行ったぞ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '一也', 'え！……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '福原', 'お母に会う気になったんだろ');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '一也', '……なんで早く言ってくれないんですか');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '福原', 'だってさっきだもん。名護からバスに乗るって――');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'description', NULL, '最後まで聞かずに飛び出していく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'description', NULL, '微笑んで見送る福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'description', NULL, 'ふと見ると、まだ並んで立っている仲村と七海。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '仲村', 'あの、ボクたちの馴れ初めをお話しましょうか――');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'dialogue', '福原', 'いい……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'description', NULL, '黙々とカレーを食べる福原。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (128, 'description', NULL, '笑うしかない仲村と七海。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (129, 'description', NULL, 'ロータリーの一番端で待つミチルの前に、那覇行きのバスが入ってくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (130, 'description', NULL, 'ロードバイクを走らせる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, 'ミチルが、バッグを手にバスに乗り込んでいく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, '一也のロードバイクが滑り込んでくる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, '那覇行きのバスに向かっていく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, 'ミチルを見つけると、座席の横の窓を叩く。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, 'バスの窓から、驚いたように一也を見るミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, '「開けろ！」という一也の声に、窓を開けるミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, '一也が息を整えながら、ミチルに向かって手を差し出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'dialogue', 'ミチル', '（きょとんとして）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'dialogue', '一也', '……（握手！）');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'dialogue', 'ミチル', '……');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, '一瞬ためらうが、一也の手を握るミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, '微笑むミチルと一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, 'ミチル、真っ直ぐに一也を見て、');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'dialogue', 'ミチル', 'ありがと');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'dialogue', '一也', 'え？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, 'バスがクラクションを鳴らし、動き出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, '離れていく２人の手。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, '動き出したバスの窓からミチルが顔を出し、一也に向かって叫ぶ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'dialogue', 'ミチル', 'フジの代わりにアタシが言っただけだよ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'dialogue', '一也', '(追って)いつでもフジに会いに来いよ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, 'だんだん小さくなる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, 'ミチルを乗せたバスが、遠くなっていく。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, '窓から顔を戻し、前を向くミチル。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (131, 'description', NULL, 'その顔には、しっかりと母と向き合おうとする決意がある……。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '須藤が持参したダンボール箱から、新しく製作されたブルーカウリング型人工尾びれを取り出し、一也に手渡す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'dialogue', '須藤', 'ブルーカウリング型です');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '×　　　　×　　　　×');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'ブルーカウリング型人工尾びれが、剛によって装着される。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '見守っている一也、ユリ、仲村、須藤、飼育員たち。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '大勢の客が周りで見ている。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '剛が人工尾びれを装着し終えるが、フジは浅瀬から出ようとしない。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '不安そうに見守る一同。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'ようやく浅瀬からメインプールへ出るフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'そして、泳ぎ始める。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'フジの動きを追って移動する一同。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'dialogue', '剛', '植村');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'dialogue', '一也', 'はい？');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '剛がターゲット棒を一也の前に差し出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'え？と剛を見る一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '目で肯く剛。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '一也、剛からターゲットを受け取る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'dialogue', '剛', 'ハイジャンプ、いきます！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '剛の合図でターゲット棒を高く掲げる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'フジの助走スペースに、他のイルカたちが入らないように、飼育員たちがプールサイドにつなぎとめる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'フジが底へ向かってグンッと沈み、勢いをつける。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '底にこすりつけるようにして尾びれを蹴るフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '加速し、グングンスピードをあげる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'さらに助走していくフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '固唾を飲む、一也、須藤、仲村、剛、ユリ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'フジが垂直に上がってきて――ジャンプ！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '水を蹴ってフジが跳び出す。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '尾びれが水を離れた瞬間、一気に体を伸ばす。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'そして、口先でターゲットにタッチ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'dialogue', '一也', '（フジを見つめて）……！');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '人工尾びれから、水の粒がゆっくりと散る。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'そして、着水。潜っていくフジ。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '見守っていた客たちから、拍手と歓声が上がる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, '須藤、仲村、剛、ユリ、飼育員たちも感嘆。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (132, 'description', NULL, 'プールサイドに戻ってきたフジ、人工尾びれを振ってみせる。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (133, 'description', NULL, '軽快にロードバイクを走らせる一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (133, 'dialogue', '一也の声', '美ら海の人とイルカに、いろんなことを教わりました。オレはここが好きです。この仕事が好きです。イルカのことをもっと知りたいと思っています。だから、東京へは戻りません――');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (133, 'description', NULL, 'ポストの前で止まると、『青山陽子様』と書かれた手紙を投函する。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (133, 'dialogue', '一也の声', '――夢を追い続けていきます');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (133, 'description', NULL, '再び風を切って走っていく一也。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (134, 'description', NULL, '人工尾びれをつけて泳いでいるフジの色々な表情。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (134, 'description', NULL, 'その映像をバックに、タイトルロールがあがっていく……。');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (134, 'description', NULL, '〈ＥＮＤ〉');
INSERT INTO scripts (scene_id, type, speaker, description) VALUES (134, 'description', NULL, '18');